# 3
# 对应名字的鞋子的数据
def get_static(names):
    ssl._create_default_https_context = ssl._create_unverified_context
    headers = {
        # 浏览器
        'User-Agent': 'Mozilla/5.0 (Macintosh; Intel Mac OS X 10_14_6) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/89.0.4389.90 Safari/537.36'}

    # 网址 (url)
    url = "https://stockx.com" + names
    req = urllib.request.Request(url=url, headers=headers)
    response = urllib.request.urlopen(req)
    # html
    text = response.read().decode()
    html = bs4.BeautifulSoup(text, 'html.parser')
    # 每天的价格
    content = html.find_all("div", class_="inset css-8atqhb")
    # sys.setdfaultencoding('utf8')
    ls_size = []
    ls_price = []
    for shoes in content:
        size = shoes.contents[0].text
        price = shoes.contents[1].text
        ls_size.append(size)
        ls_price.append(price)

    name_index = "".join(filter(str.isalnum, names)).upper()
    df = pd.DataFrame(ls_price, index=ls_size, columns=[name_index])
    df = df.T
    return df


