# 全部整理成拥有所有的鞋码来保证列数相等，没有数据的一个是本来的'Bid'一个是填充的数字0
for i in range(0,len(information)):
    for size1 in all_size:
        if size1 not in information[i].columns:
            information[i].insert(information[i].shape[1],size1,0)

# 转换成dataframe
all_data = pd.concat(information)

# change columns name to fit number
c = list(all_data.columns)
for i in range(1,len(c)):
    c[i] = re.findall(r"\d+\.?\d*",c[i])
for i in range(1,len(c)):
    all_data = all_data.rename(columns={all_data.columns[i]:c[i][0]})

